/* 同时包含两个属性 */
span[name][title] { font-size: 16px; }

/* 包含部分属性值 */
span[name~="def"] { /*  */ }

span[name^="def"] { /*  */}
span[name$="def"] { /*  */}
span[name*="def"] { /*  */}

/* name=def or name starts with -def*/
span[name|="def"] { /*  */ }
